document.addEventListener("DOMContentLoaded",function(){document.querySelectorAll(".premium-mask-yes").forEach(e=>{e.querySelector(".premium-dheading-block__title").querySelectorAll("span").forEach(e=>{var t="";e.textContent.split(" ").forEach(e=>{""!==e.trim()&&(t+=' <span class="premium-mask-span">'+e+"</span>")}),e.innerHTML=t}),new Waypoint({element:e,handler:function(){e.classList.add("premium-mask-active")},offset:"50%"})})});