document.addEventListener("DOMContentLoaded",function(){document.querySelectorAll(".premium-video-box").forEach(function(o){let r=o.dataset.type;o.addEventListener("click",function(){o.classList.toggle("video-overlay-false");let e=o.querySelector("iframe, video"),t=e.getAttribute("src");setTimeout(function(){"self"===r?(o.querySelector(".premium-video-box__overlay").remove(),e.play()):e.setAttribute("src",t.replace("autoplay=0","autoplay=1"))},300)})})});